#include<bits/stdc++.h>using namespace std;int main(){    int n,i,j,k,l;    while(cin>>i>>j && (i,j)!=EOF)    {        cout<<i<<" "<<j<<" ";        if(i>j)swap(i,j);        int ans=0;        for(int a=i; a<=j; a++)        {            int  k=a;            int l=1;            while(k!=1)            {                l++;                if(k%2!=0)                    k=3*k+1;                else                    k=k/2;            }            if(ans<=l)                ans=l;        }        cout<<ans<<endl;    }    return 0;}