#include<bits/stdc++.h>using namespace std;int main(){    int n,x,y;    while(cin>>n)    {        int ar[n],coun=0,a[n];        for(int i=0; i<n; i++)        {            cin>>ar[i];        }        int k,j,res=0;        for(int i=0,k=0; i<n-1,k<n-1; i++,k++)        {            j=i+1;            {                a[k]=fabs(ar[i]-ar[j]);               if(a[k]<=n-1 && a[k]>0)                coun++;            }        }        if(coun==n-1)        {            for(int m=0;m<n-2;m++)            {               int s=m+1;                if(fabs(a[m]-a[s])==1)                    res++;            }        }        if(res==n-2)            cout<<"Jolly"<<endl;        else            cout<<"Not jolly"<<endl;    }    return 0;}