#include<bits/stdc++.h>using namespace std;int main(){    double a,b,c;    while (cin>>a>>b>>c &&(a,b,c)!=EOF)    {        if(a+b>c&&b+c>a&&a+c>b)        {            double x=(2*((a*a)*(b*b)+(b*b)*(c*c)+(c*c)*(a*a)))-((a*a)*(a*a)+(b*b)*(b*b)+(c*c)*(c*c));            double sqr=sqrt(x);            double ans=(1.0/3.0)*sqr;            printf("%.3f\n",ans);        }        else          printf("-1.000\n");    }    return 0;}