#include<bits/stdc++.h>using namespace std;long long int tem(long long int n){   long long int sum=0,re,k;    int x=log10(n)+1;    for(int i=1; i<=x; i++)    {        k=n/10;        re=n%10;        sum=sum+re;        n=k;    }    return sum;}int main(){    long long int num,s;    while(cin>>num)    {        if(num==0)            return 0;        s=tem(num);        while(s>9)        {            s=tem(s);        }        cout<<s<<endl;    } return 0;}