#include<bits/stdc++.h>using namespace std;int main(){    int a,b,c,d,n;    cin>>n;    while(n--)    {        cin>>a>>b>>c>>d;        if(a==b && b==c && c==d)            cout<<"square"<<endl;        else if(a==c&&b==d || a==b&&c==d || a==d&&b==c)            cout<<"rectangle"<<endl;        else if(a+b+c>d && a+b+d>c && a+d+c>b && d+b+c>a)            cout<<"quadrangle"<<endl;        else            cout<<"banana"<<endl;    }    return 0;}