#include<bits/stdc++.h>using namespace std;int main(){    int n,d,r,sum;    while(cin>>n)    {        if(n==0)            return 0;        int sum=0;        while(n>=3)        {            d=n/3;            r=n%3;            n=r+d;            sum=sum+d;        }        if(n%2==0)            cout<<sum+1<<endl;        else            cout<<sum<<endl;    }    return 0;}