#include<bits/stdc++.h>using namespace std;int main(){    char st[10];    int x,t;    cin>>t;    getchar();    while(t--)    {        gets(st);        x=strlen(st);        if(x==3)        {            if(st[1]=='n'&&st[2]=='e' || st[0]=='o'&&st[2]=='e' || st[0]=='o'&&st[1]=='n')            {                cout<<"1"<<endl;            }            else            {                cout<<"2"<<endl;            }        }        else        {            cout<<"3"<<endl;        }    }}